import React, { useState } from 'react';
import './AboutCollapse.css';

const AboutCollapse = () => {
  const sections = ['BAYANIHAN ACT', 'CARDS AND ACCOUNTS', 'UNIONBANK ONLINE', 'LOANS', 'MSME and Corporate', 'Wealth', 'OTHERS'];
  const [openSection, setOpenSection] = useState(null);

  return (
    <div className="about-collapse">
      <div className="left-section">
        <h1>ABOUT US</h1>
        <p>Here are answers that we hope will help you out. If you can't find the questions you're looking for, write to us on our Contact Page.</p>
      </div>
      <div className="right-section">
        {sections.map((section) => (
          <div key={section} className="collapsible-section">
            <button onClick={() => setOpenSection(openSection === section ? null : section)}>{section}</button>
            {openSection === section && <p>Placeholder content for {section}</p>}
          </div>
        ))}
      </div>
    </div>
  );
}

export default AboutCollapse;
