import React, { useState } from 'react';
import './AboutCollapse.css';
import colArrow from '../../Assets/arrow.png'; // Import the arrow image

const AboutCollapse = () => {
  const sections = {
    // ... (rest of your sections)
  };
  const [openSection, setOpenSection] = useState(null);

  return (
    <div className="about-collapse">
      <div className="left-section">
        <h1 style={{fontSize: '4em'}}>ABOUT US</h1>
        <p style={{fontSize: '1.5em'}}>What about Original Beef Tapa Flakes? You are currently in the about page where you can check our contact details, payment methods, and our terms service, alongside our refund policy. Do read carefully and feel free to email us using the email in the Contact section to reach out to us for any other concern that wasnt mentioned in our FAQ.</p>
      </div>
      <div className="right-section">
        {Object.keys(sections).map((section, index) => (
          <div key={section} className="collapsible-section" style={{borderBottom: index !== Object.keys(sections).length - 1 ? '1px solid black' : 'none', marginBottom: '10px'}}>
            <button onClick={() => setOpenSection(openSection === section ? null : section)} style={{outline: 'none', background: 'none', backgroundImage: `url(${colArrow})`, backgroundPosition: 'right', backgroundRepeat: 'no-repeat', backgroundSize: openSection === section ? '20px' : '0px'}}>{section}</button>
            {openSection === section && <p>{sections[section]}</p>}
          </div>
        ))}
      </div>
    </div>
  );
}

export default AboutCollapse;
