import React, { useState } from 'react';
import './AboutCollapse.css';

const AboutCollapse = () => {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <div className="about-collapse">
      <div className="left-section">
        <h1>ABOUT US</h1>
        <p>Here are answers that we hope will help you out. If you can't find the questions you're looking for, write to us on our Contact Page.</p>
      </div>
      <div className="right-section">
        {['BAYANIHAN ACT', 'CARDS AND ACCOUNTS', 'UNIONBANK ONLINE', 'LOANS', 'MSME and Corporate', 'Wealth', 'OTHERS'].map((section) => (
          <div key={section} className="collapsible-section">
            <button onClick={() => setIsOpen(!isOpen)}>{section}</button>
            {isOpen && <p>Placeholder content for {section}</p>}
          </div>
        ))}
      </div>
    </div>
  );
}

export default AboutCollapse;
