import React, { useState } from 'react';
import './AboutCollapse.css';

const AboutCollapse = () => {
  const sections = {
    'SHIPPING': ' We ship via Lalamove on Mondays-Saturdays if orders are within the service area (next-day delivery) and via Lazada if outside. For Lazada COD orders, we ask for 300/package downpayment to secure that partners will receive the products. Failure to do so will forfeit the downpayment. ',
    'CARDS AND ACCOUNTS': 'Service Hours: Our facebook page is open from 8am- 12mn.',
    'UNIONBANK ONLINE': 'Contact Info: Email address--theoriginalbeeftapaflakes@gmail.com; Fb page-- The Original Beef Tapa Flakes MAIN',
    'LOANS': 'Available Payment Options: BDO Savings - 005430247449, Kristine Gail Sandoval',
    'MSME and Corporate': 'RCBC Checking/ RCBC Diskartech: 7590702855, Kristine Gail Sandoval-Zamora',
    'Wealth': 'LIMITED to BPI to BPI only: 8489340519 Kristine Gail Sandoval',
    'OTHERS': 'GCASH: 0963 690 9006 Kristine Gail S., 0917 426 4330 Raul Zamora Jr., UNIONBANK Savings: 1093 4042 0640 Kristine Gail Sandoval-Zamora'
  };
  const [openSection, setOpenSection] = useState(null);

  return (
    <div className="about-collapse">
      <div className="left-section">
        <h1 style={{fontSize: '2em'}}>ABOUT US</h1> {/* Increase font size */}
        <p>Here are answers that we hope will help you out. If you can't find the questions you're looking for, write to us on our Contact Page.</p>
      </div>
      <div className="right-section">
        {Object.keys(sections).map((section, index) => (
          <div key={section} className="collapsible-section" style={{borderBottom: index !== Object.keys(sections).length - 1 ? '1px solid black' : 'none', marginBottom: '10px'}}>
            {/* Add border-bottom and margin-bottom */}
            <button onClick={() => setOpenSection(openSection === section ? null : section)} style={{outline: 'none'}}>{section}</button> {/* Remove outline */}
            {openSection === section && <p>{sections[section]}</p>}
          </div>
        ))}
      </div>
    </div>
  );
}

export default AboutCollapse;
